
class Packages {
  get yaml() { return super.yaml = require('yaml') }
  get big()  { return super.big  = require('big.js') }
  get pool() { return super.pool = require('generic-pool') }
  get xmlbuilder() { return super.xmlbuilder = require('xmlbuilder') }
}

/** @type require &  Packages */
module.exports = exports = Object.setPrototypeOf (require, new Packages)
