const cds = require('../cds')

class Outbox extends cds.Service {
  constructor(...args) {
    super(...args)

    if (this.options.outbox) return cds.outboxed(this)
  }
}

module.exports = cds.utils.deprecated(() => Outbox, {
  kind: 'Class',
  old: 'Outbox',
  use: 'config or cds.outboxed() to outbox your service'
})()
