const SelectBuilder = require('../../db/sql-builder').SelectBuilder

class CustomSelectBuilder extends SelectBuilder {
  getCollate() {
    return 'COLLATE ' + this.getCollatingSequence()
  }

  getCollatingSequence() {
    return 'NOCASE'
  }

  _forUpdate() {}
  _forShareLock() {}
}

module.exports = CustomSelectBuilder
